<?phpsession_start();include '../conexion/consulSQL.php';sleep(1);if ($_POST['valor'] <> "") {    $result = ejecutarSQL::consultar("SELECT * FROM producto WHERE (estado_prod='Disponible' OR estado_prod='Agotado') AND (producto.nombre_prod REGEXP '" . $_POST['valor'] . "' OR producto.marca REGEXP '" . $_POST['valor'] . "' OR producto.descripcion_prod REGEXP '" . $_POST['valor'] . "' OR producto.codigo_prod REGEXP '" . $_POST['valor'] . "')");    if ($result->num_rows > 0) {        ?>        <section id="new-prod-index">            <div class="container">                <div class="row">                    <?php                    $nums = 1;                    while ($fila = mysqli_fetch_array($result)) {                        ?>                        <div class="col-xs-12 col-sm-6 col-md-3">                            <div class="thumbnail" style="width: 90%">                                <a onclick="modal_ver_producto(<?php echo $fila['id']; ?>)" ><img style="max-width: 70%" src="Recursos/img-products/<?php echo $fila['imagen'] ?>" data-toggle="popover" data-trigger="hover" data-content="<?php echo $fila['descripcion_prod'] ?>"></a>                                <div class="caption">                                    <h3><?php echo $fila['nombre_prod'] ?></h3>                                    <p><?php echo $fila['marca'] ?></p>                                    <p>$<?php echo $fila['precio'] ?></p>                                    <p class="text-center">                                        <a onclick="modal_ver_producto(<?php echo $fila['id']; ?>)" class="btn btn-primary btn-sm"><i class="fa fa-plus"></i>&nbsp; Detalles</a>&nbsp;&nbsp;                                        <?php                                        if (isset($_SESSION['nombreUser']) || empty($_SESSION['nombreAdmin'])) {                                            if ($fila['estado_prod'] <> 'Agotado') {                                                ?>                                                <button value="<?php echo $fila['id']; ?>" class="btn btn-success btn-sm botonCarrito"><i class="fa fa-shopping-cart"></i>&nbsp; AÃ±adir</button>                                                <?php                                            }                                        }                                        ?>                                    </p>                                </div>                            </div>                        </div>                        <?php                        if ($nums % 4 == 0) {                            echo '<div class="clearfix"></div>';                        }                        $nums++;                    }                    ?>                  </div>            </div>        </section>        <?php    } else {        echo '<img src="recursos/img/incorrecto.png" class="center-all-contens"><br><p class="lead text-center">Ha ocurrido un error.<br>Por favor intente nuevamente</p>';    }}else{    echo '<img src="recursos/img/incorrecto.png" class="center-all-contens"><br><p class="lead text-center">Ingrese un valor para buscar</p>';}