<?phpsession_start();include '../Conexion/consulSQL.php';sleep(1);$codeCateg= $_POST['categ-code'];$nameCateg= $_POST['categ-name'];$descripCateg= $_POST['categ-descrip'];if(!$codeCateg=="" && !$nameCateg=="" && !$descripCateg==""){    $verificar=  ejecutarSQL::consultar("select * from categoria where codigo_categoria='".$codeCateg."'");    $verificaltotal = mysqli_num_rows($verificar);    if($verificaltotal<=0){        if(consultasSQL::InsertSQL("categoria", "codigo_categoria, Nombre, Descripcion", "'$codeCateg','$nameCateg','$descripCateg'")){            echo '<img src="Recursos/img/correcto.png" class="center-all-contens"><br><p class="lead text-center">Categoría añadida éxitosamente</p>';        }else{           echo '<img src="Recursos/img/incorrecto.png" class="center-all-contens"><br><p class="lead text-center">Ha ocurrido un error.<br>Por favor intente nuevamente</p>';         }    }else{        echo '<img src="Recursos/img/incorrecto.png" class="center-all-contens"><br><p class="lead text-center">El código que ha ingresado ya existe.<br>Por favor ingrese otro código</p>';    }}else {    echo '<img src="Recursos/img/incorrecto.png" class="center-all-contens"><br><p class="lead text-center">Error los campos no deben de estar vacíos</p>';}