<!DOCTYPE html><html lang="es">    <head>        <title>Buscar Productos</title>      </head>    <body id="container-page-product">        <section id="store">            <br>            <div class="container">                <div class="page-header" id="buscar_prod">                    <h1>Buscar Productos</h1>                    <!--Formulario para ingresar el valor a buscar-->                    <form class="form-horizontal" method="post" action="DAO/productoDAO.php">                        <input type="search" class="form-control" title="Que producto estas buscando" name="valor" style="width: 60%">                                                                        <button type="submit" name="submit" accesskey="13" style="display: none"></button>                        <input type="text" name="funcion" style="display: none" value="buscarProducto">                    </form>                </div>            </div>            <!--Div para imprimir el resultado-->            <div id="searchCarrito" style="width: 100%; text-align: center; margin: 0;"></div>        </section>        <div class="modal fade" id="verProducto" tabindex="-2" role="dialog" aria-labelledby="mySmallModalLabel" aria-hidden="true" style="padding: 20px;"></div>        <script type="text/javascript">            $(document).ready(function () {                                //Metodo ajax que realiza la consulta de la clase DAO y la imprime en el div seleccionado                //al hacer submit al formulario que se encuentra dentro del div llamado buscar_prod                $('#buscar_prod form').submit(function (e) {                    e.preventDefault();                    var informacion = $('#buscar_prod form').serialize();                    var metodo = $('#buscar_prod form').attr('method');                    var peticion = $('#buscar_prod form').attr('action');                    $.ajax({                        type: metodo,                        url: peticion,                        data: informacion,                        beforeSend: function () {                            $("#searchCarrito").html('Buscando productos <br><img src="recursos/img/enviando.gif" class="center-all-contens">');                        },                        error: function () {                            $("#searchCarrito").html("Ha ocurrido un error en el sistema");                        },                        success: function (data) {                            $("#searchCarrito").html(data);                        }                    });                    return false;                });                $("[type='number']").keypress(function (evt) {                    evt.preventDefault();                });            });            // Metodo para lanzar los detalles del producto en una ventana modal en el div verPrducto            function modal_ver_producto(id) {                $('#verProducto').load("Vista/verProducto.php?id=" + id);                $("#verProducto").modal("toggle");            }        </script>    </body></html>