<?phpinclude './library/consulSQL.php';?><!DOCTYPE html><html lang="es">    <head>        <title>Buscar Productos</title>      </head>    <body id="container-page-product">        <section id="store">            <br>            <div class="container">                <div class="page-header" id="buscar_prod">                    <h1>Buscar Productos</h1>                    <form class="form-horizontal" method="post" name="rev_visitante" action="DAO/producto.php">                        <input type="search" class="form-control" title="Ingrese la informaciÃ³n del producto que desea buscar" name="valor" style="width: 60%">                                                <button type="submit" name="submit" accesskey="13" style="display: none"></button>                        <input type="text" name="funcion" style="display: none" value="buscarProducto">                    </form>                </div>            </div>            <div id="productosBuscados" style="width: 100%; text-align: center; margin: 0;"></div>        </section>        <script type="text/javascript">            $(document).ready(function () {                $('#buscar_prod form').submit(function (e) {                    e.preventDefault();                    var informacion = $('#buscar_prod form').serialize();                    var metodo = $('#buscar_prod form').attr('method');                    var peticion = $('#buscar_prod form').attr('action');                    $.ajax({                        type: metodo,                        url: peticion,                        data: informacion,                        beforeSend: function () {                            $("#productosBuscados").html('Buscando productos <br><img src="recursos/img/enviando.gif" class="center-all-contens">');                        },                        error: function () {                            $("#productosBuscados").html("Ha ocurrido un error en el sistema");                        },                        success: function (data) {                            $("#productosBuscados").html(data);                        }                    });                    return false;                });            });            function modal_ver_producto(id) {                $('#verProducto').load("process/verProducto.php?id=" + id);                $("#verProducto").modal("toggle");            }        </script>    </body></html>