<?phpsession_start();include '../library/configServer.php';include '../library/consulSQL.php';sleep(1);$id = $_POST['id'];$vec = mysqli_fetch_row(ejecutarSQL::consultar("SELECT estado FROM administrador WHERE id=$id"));if (isset($vec) && $vec[0] <> "") {    if ($vec[0] == "Activo") {        $consA = ejecutarSQL::consultar("UPDATE administrador SET estado='Inactivo' WHERE id=$id");    } else {        $consA = ejecutarSQL::consultar("UPDATE administrador SET estado='Activo' WHERE id=$id");    }    if ($consA) {        echo '<img src="assets/img/correcto.png" class="center-all-contens"><br><p class="lead text-center">Se cambió el estado</p>';    } else {        echo '<img src="assets/img/incorrecto.png" class="center-all-contens"><br><p class="lead text-center">rror al cambiar el estado</p>';    }} else {    echo '<img src="assets/img/incorrecto.png" class="center-all-contens"><br><p class="lead text-center">No se encontró el id del administrador</p>';}