<?phpinclude '../library/consulSQL.php';sleep(1);$id = $_POST['id'];$sql = "SELECT estado_prod, imagen FROM producto WHERE id=" . $id;$vec = mysqli_fetch_row(ejecutarSQL::consultar($sql));if (isset($vec[0]) && $vec[0] <> "") {    if ($vec[0] == "Disponible") {        $sqlAc = "UPDATE producto SET estado_prod='No disponible' WHERE id=" . $id;    } else {        $sqlAc = "UPDATE producto SET estado_prod='Disponible' WHERE id=" . $id;    }    $result = ejecutarSQL::consultar($sqlAc);    if ($result) {        echo '<img src="assets/img/correcto.png" style="width: 20%" class="center-all-contens"><br><p class="lead text-center">El producto se actualizó exitosamente</p>';    } else {        echo '<img src="assets/img/incorrecto.png"  style="width: 20%" class="center-all-contens"><br><p class="lead text-center">Ha ocurrido un error.<br>Por favor intente nuevamente</p>';    }} else {    echo '<img src="assets/img/incorrecto.png" style="width: 20%" class="center-all-contens"><br><p class="lead text-center">El código del producto no existe</p>';}