<?phpinclude '../library/consulSQL.php';sleep(1);$id = $_POST['id'];$sql = "SELECT estado FROM publicacion WHERE id=" . $id;$vec = mysqli_fetch_row(ejecutarSQL::consultar($sql));if (isset($vec[0]) && $vec[0] <> "") {    if ($vec[0] == "Activo") {        $sqlAc = "UPDATE publicacion SET estado='Inactivo' WHERE id=" . $id;    } else {        $sqlAc = "UPDATE publicacion SET estado='Activo' WHERE id=" . $id;    }    $result = ejecutarSQL::consultar($sqlAc);    if ($result) {        echo '<img src="assets/img/correcto.png" style="width: 20%" class="center-all-contens"><br><p class="lead text-center">La publicación se actualizó exitosamente</p>';    } else {        echo '<img src="assets/img/incorrecto.png"  style="width: 20%" class="center-all-contens"><br><p class="lead text-center">Ha ocurrido un error.<br>Por favor intente nuevamente</p>';    }} else {    echo '<img src="assets/img/incorrecto.png" style="width: 20%" class="center-all-contens"><br><p class="lead text-center">El id de la publicación no existe</p>';}