<?phpinclude '../library/consulSQL.php';sleep(1);$nombre_completo= $_POST['nombre_completo'];$Nombre= $_POST['Nombre'];$Clave= md5($_POST['Clave']);if(!$nombre_completo=="" && !$Nombre=="" && !$Clave==""){    $verificar=  ejecutarSQL::consultar("SELECT * FROM administrador WHERE Nombre='".$Nombre."'");    $verificaltotal = mysqli_num_rows($verificar);    if($verificaltotal<=0){        if(consultasSQL::InsertSQL("administrador", "nombre_completo, Nombre, Clave, estado", "'$nombre_completo','$Nombre','$Clave','Activo'")){            echo '<img src="assets/img/correcto.png" class="center-all-contens"><br><p class="lead text-center">Administrador añadido éxitosamente</p>';        }else{           echo '<img src="assets/img/incorrecto.png" class="center-all-contens"><br><p class="lead text-center">Ha ocurrido un error.<br>Por favor intente nuevamente</p>';         }    }else{        echo '<img src="assets/img/incorrecto.png" class="center-all-contens"><br><p class="lead text-center">El nombre que ha ingresado ya existe.<br>Por favor ingrese otro nombre</p>';    }}else {    echo '<img src="assets/img/incorrecto.png" class="center-all-contens"><br><p class="lead text-center">Error los campos no deben de estar vacíos</p>';}