<?phpinclude '../library/consulSQL.php';sleep(1);$id = $_POST['id'];$sql = "SELECT nit FROM cliente WHERE id=" . $id;$vec = mysqli_fetch_row(ejecutarSQL::consultar($sql));if (isset($vec[0]) && $vec[0] <> "") {    $clave = md5($vec[0]);    $sqlAc = "UPDATE cliente SET clave='".$clave."' WHERE id=" . $id;    $result = ejecutarSQL::consultar($sqlAc);    if ($result) {        echo '<img src="assets/img/correcto.png" style="width: 20%" class="center-all-contens"><br><p class="lead text-center">Se restauró la contraseña al nit o cédula</p>';    } else {        echo '<img src="assets/img/incorrecto.png"  style="width: 20%" class="center-all-contens"><br><p class="lead text-center">Ha ocurrido un error.<br>Por favor intente nuevamente</p>';    }} else {    echo '<img src="assets/img/incorrecto.png" style="width: 20%" class="center-all-contens"><br><p class="lead text-center">El documento del Cliente no existe</p>';}