<?phpinclude '../library/consulSQL.php';sleep(2);$id = $_POST['id'];$nit = $_POST['nit'];$nombre_completo = $_POST['nombre_completo'];$direccion = $_POST['direccion'];$telefono = $_POST['telefono'];$email = $_POST['email'];$usuario = $_POST['usuario'];$clave = md5($_POST['clave']);if (!$nit == "" && !$nombre_completo == "" && !$direccion == "" && !$telefono == "" && !$email == "" && !$usuario == "" && !$clave == "") {    $verificar = ejecutarSQL::consultar("UPDATE cliente SET nit='" . $nit . "', nombre_completo='".$nombre_completo."',direccion='".$direccion."',telefono='".$telefono."',email='".$email."',usuario='".$usuario."',clave='". $clave."' WHERE id=".$id);        if ($verificar) {        echo '<img src="assets/img/ok.png" class="center-all-contens"><br>El registro se completo con éxito<script>        setTimeout(function () {            url = "editar_cliente.php";            $(location).attr("href", url);        }, 1000);    </script>';    } else {        echo '<img src="assets/img/error.png" class="center-all-contens"><br>Ha ocurrido un error.<br>Por favor informenos del error';    }} else {    echo '<img src="assets/img/error.png" class="center-all-contens"><br>Error, los campos no deben de estar vacíos';}