<?phpsession_start();include '../library/consulSQL.php';sleep(1);if (isset($_POST['id'])) {    $id = $_POST['id'];    $title = $_POST['title'];    $start = $_POST['start'];    $end = $_POST['end'];    $color = $_POST['color'];    $textcolor = $_POST['textcolor'];    $descripcion = $_POST['descripcion'];    if (!$title == "" && !$start == "" && !$end == "" && !$color == "" && !$textcolor == "") {        if (consultasSQL::UpdateSQL("events", "title='$title',start='$start',end='$end',color='$color',	textcolor='$textcolor', descripcion='$descripcion'", "id='$id'")) {            echo '<img src="assets/img/correcto.png" class="center-all-contens"><br><p class="lead text-center">Evento actualizado éxitosamente</p><script>        setTimeout(function () {            url = "agenda.php";            $(location).attr("href", url);        }, 1000);    </script>';        } else {            echo '<img src="assets/img/incorrecto.png" class="center-all-contens"><br><p class="lead text-center">Ha ocurrido un error.<br>Por favor intente nuevamente</p>';        }    } else {        echo '<img src="assets/img/incorrecto.png" class="center-all-contens"><br><p class="lead text-center">Error, campos vacíos</p>';    }} else if (isset($_POST['id2'])) {    $id2 = $_POST['id2'];    if (consultasSQL::DeleteSQL("events", "id='$id2'")) {        echo '<img src="assets/img/correcto.png" class="center-all-contens"><br><p class="lead text-center">Se eliminó el evento exitosamente</p><script>        setTimeout(function () {            url = "agenda.php";            $(location).attr("href", url);        }, 1000);';    }else{        echo '<img src="assets/img/incorrecto.png" class="center-all-contens"><br><p class="lead text-center">Error al realizar la operación</p>';    }} else {    echo '<img src="assets/img/incorrecto.png" class="center-all-contens"><br><p class="lead text-center">No se reconoce ningun evento para eliminar</p>';}